    robots: robots,
    searchfield: ''
}

class App extends Component {
    constructor(){
        super()
        this.state = {
            robots: robots,
            searchfield: ''
        }
    }
    onSearchChange(event){
        console.log(event.target.value);
        const filteredRobots = this.state.robots.filter(robot =>{
